#include "AuthPayLoadObject.h"
#include "UserObject.h"

#include "graphqlservice/internal/Schema.h"

#include "graphqlservice/introspection/IntrospectionSchema.h"

#include <algorithm>
#include <functional>
#include <sstream>
#include <stdexcept>
#include <unordered_map>

using namespace std::literals;

namespace graphql::omnisphere {
namespace object {

AuthPayLoad::AuthPayLoad(std::unique_ptr<const Concept> pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames AuthPayLoad::getTypeNames() const noexcept
{
	return {
		R"gql(AuthPayLoad)gql"sv
	};
}

service::ResolverMap AuthPayLoad::getResolvers() const noexcept
{
	return {
		{ R"gql(User)gql"sv, [this](service::ResolverParams&& params) { return resolveUser(std::move(params)); } },
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } },
		{ R"gql(AccessToken)gql"sv, [this](service::ResolverParams&& params) { return resolveAccessToken(std::move(params)); } }
	};
}

void AuthPayLoad::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void AuthPayLoad::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver AuthPayLoad::resolveAccessToken(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getAccessToken(service::FieldParams { std::move(selectionSetParams), std::move(directives) });
	resolverLock.unlock();

	return service::ModifiedResult<std::string>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver AuthPayLoad::resolveUser(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getUser(service::FieldParams { std::move(selectionSetParams), std::move(directives) });
	resolverLock.unlock();

	return service::ModifiedResult<User>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver AuthPayLoad::resolve_typename(service::ResolverParams&& params) const
{
	return service::Result<std::string>::convert(std::string{ R"gql(AuthPayLoad)gql" }, std::move(params));
}

} // namespace object

void AddAuthPayLoadDetails(const std::shared_ptr<schema::ObjectType>& typeAuthPayLoad, const std::shared_ptr<schema::Schema>& schema)
{
	typeAuthPayLoad->AddFields({
		schema::Field::Make(R"gql(AccessToken)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(String)gql"sv))),
		schema::Field::Make(R"gql(User)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(User)gql"sv)))
	});
}

} // namespace graphql::omnisphere
