ItemBrandQuery::ItemBrandQuery(std::unique_ptr<const Concept> pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames ItemBrandQuery::getTypeNames() const noexcept
{
	return {
		R"gql(ItemBrandQuery)gql"sv
	};
}

service::ResolverMap ItemBrandQuery::getResolvers() const noexcept
{
	return {
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } },
		{ R"gql(GetItemBrand)gql"sv, [this](service::ResolverParams&& params) { return resolveGetItemBrand(std::move(params)); } },
		{ R"gql(GetAllItemBrands)gql"sv, [this](service::ResolverParams&& params) { return resolveGetAllItemBrands(std::move(params)); } }
	};
}

void ItemBrandQuery::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void ItemBrandQuery::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver ItemBrandQuery::resolveGetItemBrand(service::ResolverParams&& params) const
{
	auto argItemBrand = service::ModifiedArgument<omnisphere::GetItemBrandInput>::require("ItemBrand", params.arguments);
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getGetItemBrand(service::FieldParams { std::move(selectionSetParams), std::move(directives) }, std::move(argItemBrand));
	resolverLock.unlock();

	return service::ModifiedResult<ItemBrand>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver ItemBrandQuery::resolveGetAllItemBrands(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getGetAllItemBrands(service::FieldParams { std::move(selectionSetParams), std::move(directives) });
	resolverLock.unlock();

	return service::ModifiedResult<ItemBrand>::convert<service::TypeModifier::List>(std::move(result), std::move(params));
}

service::AwaitableResolver ItemBrandQuery::resolve_typename(service::ResolverParams&& params) const
{
	return service::Result<std::string>::convert(std::string{ R"gql(ItemBrandQuery)gql" }, std::move(params));
}

