#include "ItemGroupQueryObject.h"
#include "ItemGroupObject.h"

#include "graphqlservice/internal/Schema.h"

#include "graphqlservice/introspection/IntrospectionSchema.h"

#include <algorithm>
#include <functional>
#include <sstream>
#include <stdexcept>
#include <unordered_map>

using namespace std::literals;

namespace graphql::omnisphere {
namespace object {

ItemGroupQuery::ItemGroupQuery(std::unique_ptr<const Concept> pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames ItemGroupQuery::getTypeNames() const noexcept
{
	return {
		R"gql(ItemGroupQuery)gql"sv
	};
}

service::ResolverMap ItemGroupQuery::getResolvers() const noexcept
{
	return {
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } },
		{ R"gql(GetItemGroup)gql"sv, [this](service::ResolverParams&& params) { return resolveGetItemGroup(std::move(params)); } },
		{ R"gql(GetAllItemGroups)gql"sv, [this](service::ResolverParams&& params) { return resolveGetAllItemGroups(std::move(params)); } }
	};
}

void ItemGroupQuery::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void ItemGroupQuery::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver ItemGroupQuery::resolveGetItemGroup(service::ResolverParams&& params) const
{
	auto argItemGroup = service::ModifiedArgument<omnisphere::GetItemGroupInput>::require("ItemGroup", params.arguments);
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getGetItemGroup(service::FieldParams { std::move(selectionSetParams), std::move(directives) }, std::move(argItemGroup));
	resolverLock.unlock();

	return service::ModifiedResult<ItemGroup>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver ItemGroupQuery::resolveGetAllItemGroups(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getGetAllItemGroups(service::FieldParams { std::move(selectionSetParams), std::move(directives) });
	resolverLock.unlock();

	return service::ModifiedResult<ItemGroup>::convert<service::TypeModifier::List>(std::move(result), std::move(params));
}

service::AwaitableResolver ItemGroupQuery::resolve_typename(service::ResolverParams&& params) const
{
	return service::Result<std::string>::convert(std::string{ R"gql(ItemGroupQuery)gql" }, std::move(params));
}

} // namespace object

void AddItemGroupQueryDetails(const std::shared_ptr<schema::ObjectType>& typeItemGroupQuery, const std::shared_ptr<schema::Schema>& schema)
{
	typeItemGroupQuery->AddFields({
		schema::Field::Make(R"gql(GetItemGroup)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(ItemGroup)gql"sv)), {
			schema::InputValue::Make(R"gql(ItemGroup)gql"sv, R"md()md"sv, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(GetItemGroupInput)gql"sv)), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(GetAllItemGroups)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->WrapType(introspection::TypeKind::LIST, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(ItemGroup)gql"sv)))))
	});
}

} // namespace graphql::omnisphere
