#include "ItemMutationObject.h"
#include "ItemObject.h"

#include "graphqlservice/internal/Schema.h"

#include "graphqlservice/introspection/IntrospectionSchema.h"

#include <algorithm>
#include <functional>
#include <sstream>
#include <stdexcept>
#include <unordered_map>

using namespace std::literals;

namespace graphql::omnisphere {
namespace object {

ItemMutation::ItemMutation(std::unique_ptr<const Concept> pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames ItemMutation::getTypeNames() const noexcept
{
	return {
		R"gql(ItemMutation)gql"sv
	};
}

service::ResolverMap ItemMutation::getResolvers() const noexcept
{
	return {
		{ R"gql(CreateItem)gql"sv, [this](service::ResolverParams&& params) { return resolveCreateItem(std::move(params)); } },
		{ R"gql(UpdateItem)gql"sv, [this](service::ResolverParams&& params) { return resolveUpdateItem(std::move(params)); } },
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } }
	};
}

void ItemMutation::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void ItemMutation::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver ItemMutation::resolveCreateItem(service::ResolverParams&& params) const
{
	auto argItem = service::ModifiedArgument<omnisphere::CreateItemInput>::require("Item", params.arguments);
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getCreateItem(service::FieldParams { std::move(selectionSetParams), std::move(directives) }, std::move(argItem));
	resolverLock.unlock();

	return service::ModifiedResult<Item>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver ItemMutation::resolveUpdateItem(service::ResolverParams&& params) const
{
	auto argItem = service::ModifiedArgument<omnisphere::UpdateItemInput>::require("Item", params.arguments);
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getUpdateItem(service::FieldParams { std::move(selectionSetParams), std::move(directives) }, std::move(argItem));
	resolverLock.unlock();

	return service::ModifiedResult<Item>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver ItemMutation::resolve_typename(service::ResolverParams&& params) const
{
	return service::Result<std::string>::convert(std::string{ R"gql(ItemMutation)gql" }, std::move(params));
}

} // namespace object

void AddItemMutationDetails(const std::shared_ptr<schema::ObjectType>& typeItemMutation, const std::shared_ptr<schema::Schema>& schema)
{
	typeItemMutation->AddFields({
		schema::Field::Make(R"gql(CreateItem)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Item)gql"sv)), {
			schema::InputValue::Make(R"gql(Item)gql"sv, R"md()md"sv, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(CreateItemInput)gql"sv)), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(UpdateItem)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Item)gql"sv)), {
			schema::InputValue::Make(R"gql(Item)gql"sv, R"md()md"sv, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(UpdateItemInput)gql"sv)), R"gql()gql"sv)
		})
	});
}

} // namespace graphql::omnisphere
