#pragma once

#ifndef ITEMMUTATIONOBJECT_H
#define ITEMMUTATIONOBJECT_H

#include "OmnisSchema.h"

namespace graphql::omnisphere::object {
namespace methods::ItemMutationHas {

template <class TImpl>
concept getCreateItemWithParams = requires (TImpl impl, service::FieldParams params, CreateItemInput ItemArg)
{
	{ service::AwaitableObject<std::shared_ptr<Item>> { impl.getCreateItem(std::move(params), std::move(ItemArg)) } };
};

template <class TImpl>
concept getCreateItem = requires (TImpl impl, CreateItemInput ItemArg)
{
	{ service::AwaitableObject<std::shared_ptr<Item>> { impl.getCreateItem(std::move(ItemArg)) } };
};

template <class TImpl>
concept getUpdateItemWithParams = requires (TImpl impl, service::FieldParams params, UpdateItemInput ItemArg)
{
	{ service::AwaitableObject<std::shared_ptr<Item>> { impl.getUpdateItem(std::move(params), std::move(ItemArg)) } };
};

template <class TImpl>
concept getUpdateItem = requires (TImpl impl, UpdateItemInput ItemArg)
{
	{ service::AwaitableObject<std::shared_ptr<Item>> { impl.getUpdateItem(std::move(ItemArg)) } };
};

template <class TImpl>
concept beginSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.beginSelectionSet(params) };
};

template <class TImpl>
concept endSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.endSelectionSet(params) };
};

} // namespace methods::ItemMutationHas

class [[nodiscard("unnecessary construction")]] ItemMutation final
	: public service::Object
{
private:
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveCreateItem(service::ResolverParams&& params) const;
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveUpdateItem(service::ResolverParams&& params) const;

	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolve_typename(service::ResolverParams&& params) const;

	struct [[nodiscard("unnecessary construction")]] Concept
	{
		virtual ~Concept() = default;

		virtual void beginSelectionSet(const service::SelectionSetParams& params) const = 0;
		virtual void endSelectionSet(const service::SelectionSetParams& params) const = 0;

		[[nodiscard("unnecessary call")]] virtual service::AwaitableObject<std::shared_ptr<Item>> getCreateItem(service::FieldParams&& params, CreateItemInput&& ItemArg) const = 0;
		[[nodiscard("unnecessary call")]] virtual service::AwaitableObject<std::shared_ptr<Item>> getUpdateItem(service::FieldParams&& params, UpdateItemInput&& ItemArg) const = 0;
	};

	template <class T>
	struct [[nodiscard("unnecessary construction")]] Model final
		: Concept
	{
		explicit Model(std::shared_ptr<T> pimpl) noexcept
			: _pimpl { std::move(pimpl) }
		{
		}

		[[nodiscard("unnecessary call")]] service::AwaitableObject<std::shared_ptr<Item>> getCreateItem(service::FieldParams&& params, CreateItemInput&& ItemArg) const override
		{
			if constexpr (methods::ItemMutationHas::getCreateItemWithParams<T>)
			{
				return { _pimpl->getCreateItem(std::move(params), std::move(ItemArg)) };
			}
			else
			{
				static_assert(methods::ItemMutationHas::getCreateItem<T>, R"msg(ItemMutation::getCreateItem is not implemented)msg");
				return { _pimpl->getCreateItem(std::move(ItemArg)) };
			}
		}

		[[nodiscard("unnecessary call")]] service::AwaitableObject<std::shared_ptr<Item>> getUpdateItem(service::FieldParams&& params, UpdateItemInput&& ItemArg) const override
		{
			if constexpr (methods::ItemMutationHas::getUpdateItemWithParams<T>)
			{
				return { _pimpl->getUpdateItem(std::move(params), std::move(ItemArg)) };
			}
			else
			{
				static_assert(methods::ItemMutationHas::getUpdateItem<T>, R"msg(ItemMutation::getUpdateItem is not implemented)msg");
				return { _pimpl->getUpdateItem(std::move(ItemArg)) };
			}
		}

		void beginSelectionSet(const service::SelectionSetParams& params) const override
		{
			if constexpr (methods::ItemMutationHas::beginSelectionSet<T>)
			{
				_pimpl->beginSelectionSet(params);
			}
		}

		void endSelectionSet(const service::SelectionSetParams& params) const override
		{
			if constexpr (methods::ItemMutationHas::endSelectionSet<T>)
			{
				_pimpl->endSelectionSet(params);
			}
		}

	private:
		const std::shared_ptr<T> _pimpl;
	};

	explicit ItemMutation(std::unique_ptr<const Concept> pimpl) noexcept;

	[[nodiscard("unnecessary call")]] service::TypeNames getTypeNames() const noexcept;
	[[nodiscard("unnecessary call")]] service::ResolverMap getResolvers() const noexcept;

	void beginSelectionSet(const service::SelectionSetParams& params) const override;
	void endSelectionSet(const service::SelectionSetParams& params) const override;

	const std::unique_ptr<const Concept> _pimpl;

public:
	template <class T>
	explicit ItemMutation(std::shared_ptr<T> pimpl) noexcept
		: ItemMutation { std::unique_ptr<const Concept> { std::make_unique<Model<T>>(std::move(pimpl)) } }
	{
	}

	[[nodiscard("unnecessary call")]] static constexpr std::string_view getObjectType() noexcept
	{
		return { R"gql(ItemMutation)gql" };
	}
};

} // namespace graphql::omnisphere::object

#endif // ITEMMUTATIONOBJECT_H
