Item::Item(std::unique_ptr<const Concept> pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames Item::getTypeNames() const noexcept
{
	return {
		R"gql(Item)gql"sv
	};
}

service::ResolverMap Item::getResolvers() const noexcept
{
	return {
		{ R"gql(Code)gql"sv, [this](service::ResolverParams&& params) { return resolveCode(std::move(params)); } },
		{ R"gql(Name)gql"sv, [this](service::ResolverParams&& params) { return resolveName(std::move(params)); } },
		{ R"gql(Brand)gql"sv, [this](service::ResolverParams&& params) { return resolveBrand(std::move(params)); } },
		{ R"gql(Entry)gql"sv, [this](service::ResolverParams&& params) { return resolveEntry(std::move(params)); } },
		{ R"gql(Group)gql"sv, [this](service::ResolverParams&& params) { return resolveGroup(std::move(params)); } },
		{ R"gql(Image)gql"sv, [this](service::ResolverParams&& params) { return resolveImage(std::move(params)); } },
		{ R"gql(Price)gql"sv, [this](service::ResolverParams&& params) { return resolvePrice(std::move(params)); } },
		{ R"gql(OnHand)gql"sv, [this](service::ResolverParams&& params) { return resolveOnHand(std::move(params)); } },
		{ R"gql(OnOrder)gql"sv, [this](service::ResolverParams&& params) { return resolveOnOrder(std::move(params)); } },
		{ R"gql(IsActive)gql"sv, [this](service::ResolverParams&& params) { return resolveIsActive(std::move(params)); } },
		{ R"gql(MaxOrder)gql"sv, [this](service::ResolverParams&& params) { return resolveMaxOrder(std::move(params)); } },
		{ R"gql(MaxStock)gql"sv, [this](service::ResolverParams&& params) { return resolveMaxStock(std::move(params)); } },
		{ R"gql(MinOrder)gql"sv, [this](service::ResolverParams&& params) { return resolveMinOrder(std::move(params)); } },
		{ R"gql(MinStock)gql"sv, [this](service::ResolverParams&& params) { return resolveMinStock(std::move(params)); } },
		{ R"gql(SellItem)gql"sv, [this](service::ResolverParams&& params) { return resolveSellItem(std::move(params)); } },
		{ R"gql(CreatedBy)gql"sv, [this](service::ResolverParams&& params) { return resolveCreatedBy(std::move(params)); } },
		{ R"gql(OnRequest)gql"sv, [this](service::ResolverParams&& params) { return resolveOnRequest(std::move(params)); } },
		{ R"gql(CreateDate)gql"sv, [this](service::ResolverParams&& params) { return resolveCreateDate(std::move(params)); } },
		{ R"gql(MaxRequest)gql"sv, [this](service::ResolverParams&& params) { return resolveMaxRequest(std::move(params)); } },
		{ R"gql(MinRequest)gql"sv, [this](service::ResolverParams&& params) { return resolveMinRequest(std::move(params)); } },
		{ R"gql(UpdateDate)gql"sv, [this](service::ResolverParams&& params) { return resolveUpdateDate(std::move(params)); } },
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } },
		{ R"gql(Description)gql"sv, [this](service::ResolverParams&& params) { return resolveDescription(std::move(params)); } },
		{ R"gql(PurchaseItem)gql"sv, [this](service::ResolverParams&& params) { return resolvePurchaseItem(std::move(params)); } },
		{ R"gql(InventoryItem)gql"sv, [this](service::ResolverParams&& params) { return resolveInventoryItem(std::move(params)); } },
		{ R"gql(LastUpdatedBy)gql"sv, [this](service::ResolverParams&& params) { return resolveLastUpdatedBy(std::move(params)); } }
	};
}

void Item::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void Item::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver Item::resolveEntry(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getEntry(service::FieldParams { std::move(selectionSetParams), std::move(directives) });
	resolverLock.unlock();

	return service::ModifiedResult<int>::convert(std::move(result), std::move(params));
}

