void AddItemDetails(const std::shared_ptr<schema::ObjectType>& typeItem, const std::shared_ptr<schema::Schema>& schema)
{
	typeItem->AddFields({
		schema::Field::Make(R"gql(Entry)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Int)gql"sv))),
		schema::Field::Make(R"gql(Code)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(String)gql"sv))),
		schema::Field::Make(R"gql(Name)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(String)gql"sv))),
		schema::Field::Make(R"gql(Description)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(String)gql"sv)),
		schema::Field::Make(R"gql(Image)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(String)gql"sv)),
		schema::Field::Make(R"gql(IsActive)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Boolean)gql"sv))),
		schema::Field::Make(R"gql(PurchaseItem)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Boolean)gql"sv))),
		schema::Field::Make(R"gql(SellItem)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Boolean)gql"sv))),
		schema::Field::Make(R"gql(InventoryItem)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Boolean)gql"sv))),
		schema::Field::Make(R"gql(Price)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Float)gql"sv))),
		schema::Field::Make(R"gql(Brand)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(ItemBrand)gql"sv)),
		schema::Field::Make(R"gql(Group)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(ItemGroup)gql"sv)),
		schema::Field::Make(R"gql(OnHand)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Float)gql"sv))),
		schema::Field::Make(R"gql(OnOrder)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Float)gql"sv)),
		schema::Field::Make(R"gql(OnRequest)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Float)gql"sv)),
		schema::Field::Make(R"gql(MinStock)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Float)gql"sv)),
		schema::Field::Make(R"gql(MaxStock)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Float)gql"sv)),
		schema::Field::Make(R"gql(MinOrder)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Float)gql"sv)),
		schema::Field::Make(R"gql(MaxOrder)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Float)gql"sv)),
		schema::Field::Make(R"gql(MinRequest)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Float)gql"sv)),
		schema::Field::Make(R"gql(MaxRequest)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Float)gql"sv)),
		schema::Field::Make(R"gql(CreatedBy)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(User)gql"sv))),
		schema::Field::Make(R"gql(CreateDate)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(String)gql"sv))),
		schema::Field::Make(R"gql(LastUpdatedBy)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(User)gql"sv)),
		schema::Field::Make(R"gql(UpdateDate)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(String)gql"sv))
	});
}

