#include "ItemQueryObject.h"
#include "ItemObject.h"

#include "graphqlservice/internal/Schema.h"

#include "graphqlservice/introspection/IntrospectionSchema.h"

#include <algorithm>
#include <functional>
#include <sstream>
#include <stdexcept>
#include <unordered_map>

using namespace std::literals;

namespace graphql::omnisphere {
namespace object {

ItemQuery::ItemQuery(std::unique_ptr<const Concept> pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames ItemQuery::getTypeNames() const noexcept
{
	return {
		R"gql(ItemQuery)gql"sv
	};
}

service::ResolverMap ItemQuery::getResolvers() const noexcept
{
	return {
		{ R"gql(GetItem)gql"sv, [this](service::ResolverParams&& params) { return resolveGetItem(std::move(params)); } },
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } },
		{ R"gql(GetAllItems)gql"sv, [this](service::ResolverParams&& params) { return resolveGetAllItems(std::move(params)); } },
		{ R"gql(SearchItems)gql"sv, [this](service::ResolverParams&& params) { return resolveSearchItems(std::move(params)); } }
	};
}

void ItemQuery::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void ItemQuery::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver ItemQuery::resolveGetItem(service::ResolverParams&& params) const
{
	auto argItem = service::ModifiedArgument<omnisphere::GetItemInput>::require("Item", params.arguments);
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getGetItem(service::FieldParams { std::move(selectionSetParams), std::move(directives) }, std::move(argItem));
	resolverLock.unlock();

	return service::ModifiedResult<Item>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver ItemQuery::resolveGetAllItems(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getGetAllItems(service::FieldParams { std::move(selectionSetParams), std::move(directives) });
	resolverLock.unlock();

	return service::ModifiedResult<Item>::convert<service::TypeModifier::List>(std::move(result), std::move(params));
}

service::AwaitableResolver ItemQuery::resolveSearchItems(service::ResolverParams&& params) const
{
	auto argFilter = service::ModifiedArgument<omnisphere::SearchItemsInput>::require("Filter", params.arguments);
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getSearchItems(service::FieldParams { std::move(selectionSetParams), std::move(directives) }, std::move(argFilter));
	resolverLock.unlock();

	return service::ModifiedResult<Item>::convert<service::TypeModifier::List>(std::move(result), std::move(params));
}

service::AwaitableResolver ItemQuery::resolve_typename(service::ResolverParams&& params) const
{
	return service::Result<std::string>::convert(std::string{ R"gql(ItemQuery)gql" }, std::move(params));
}

} // namespace object

void AddItemQueryDetails(const std::shared_ptr<schema::ObjectType>& typeItemQuery, const std::shared_ptr<schema::Schema>& schema)
{
	typeItemQuery->AddFields({
		schema::Field::Make(R"gql(GetItem)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Item)gql"sv)), {
			schema::InputValue::Make(R"gql(Item)gql"sv, R"md()md"sv, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(GetItemInput)gql"sv)), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(GetAllItems)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->WrapType(introspection::TypeKind::LIST, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Item)gql"sv))))),
		schema::Field::Make(R"gql(SearchItems)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->WrapType(introspection::TypeKind::LIST, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Item)gql"sv)))), {
			schema::InputValue::Make(R"gql(Filter)gql"sv, R"md()md"sv, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(SearchItemsInput)gql"sv)), R"gql()gql"sv)
		})
	});
}

} // namespace graphql::omnisphere
