template <class TImpl>
concept getGetItemWithParams = requires (TImpl impl, service::FieldParams params, GetItemInput ItemArg)
{
	{ service::AwaitableObject<std::shared_ptr<Item>> { impl.getGetItem(std::move(params), std::move(ItemArg)) } };
};

template <class TImpl>
concept getGetItem = requires (TImpl impl, GetItemInput ItemArg)
{
	{ service::AwaitableObject<std::shared_ptr<Item>> { impl.getGetItem(std::move(ItemArg)) } };
};

template <class TImpl>
concept getGetAllItemsWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableObject<std::vector<std::shared_ptr<Item>>> { impl.getGetAllItems(std::move(params)) } };
};

template <class TImpl>
concept getGetAllItems = requires (TImpl impl)
{
	{ service::AwaitableObject<std::vector<std::shared_ptr<Item>>> { impl.getGetAllItems() } };
};

template <class TImpl>
concept getSearchItemsWithParams = requires (TImpl impl, service::FieldParams params, SearchItemsInput FilterArg)
{
	{ service::AwaitableObject<std::vector<std::shared_ptr<Item>>> { impl.getSearchItems(std::move(params), std::move(FilterArg)) } };
};

template <class TImpl>
concept getSearchItems = requires (TImpl impl, SearchItemsInput FilterArg)
{
	{ service::AwaitableObject<std::vector<std::shared_ptr<Item>>> { impl.getSearchItems(std::move(FilterArg)) } };
};

template <class TImpl>
concept beginSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.beginSelectionSet(params) };
};

template <class TImpl>
concept endSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.endSelectionSet(params) };
};

