#pragma once

#ifndef MUTATIONOBJECT_H
#define MUTATIONOBJECT_H

#include "OmnisSchema.h"

namespace graphql::omnisphere::object {
namespace methods::MutationHas {

template <class TImpl>
concept applyUserWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableObject<std::shared_ptr<UserMutation>> { impl.applyUser(std::move(params)) } };
};

template <class TImpl>
concept applyUser = requires (TImpl impl)
{
	{ service::AwaitableObject<std::shared_ptr<UserMutation>> { impl.applyUser() } };
};

template <class TImpl>
concept applySessionWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableObject<std::shared_ptr<SessionMutation>> { impl.applySession(std::move(params)) } };
};

template <class TImpl>
concept applySession = requires (TImpl impl)
{
	{ service::AwaitableObject<std::shared_ptr<SessionMutation>> { impl.applySession() } };
};

template <class TImpl>
concept applyItemWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableObject<std::shared_ptr<ItemMutation>> { impl.applyItem(std::move(params)) } };
};

template <class TImpl>
concept applyItem = requires (TImpl impl)
{
	{ service::AwaitableObject<std::shared_ptr<ItemMutation>> { impl.applyItem() } };
};

template <class TImpl>
concept applyItemGroupWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableObject<std::shared_ptr<ItemGroupMutation>> { impl.applyItemGroup(std::move(params)) } };
};

template <class TImpl>
concept applyItemGroup = requires (TImpl impl)
{
	{ service::AwaitableObject<std::shared_ptr<ItemGroupMutation>> { impl.applyItemGroup() } };
};

template <class TImpl>
concept applyItemBrandWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableObject<std::shared_ptr<ItemBrandMutation>> { impl.applyItemBrand(std::move(params)) } };
};

template <class TImpl>
concept applyItemBrand = requires (TImpl impl)
{
	{ service::AwaitableObject<std::shared_ptr<ItemBrandMutation>> { impl.applyItemBrand() } };
};

template <class TImpl>
concept beginSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.beginSelectionSet(params) };
};

template <class TImpl>
concept endSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.endSelectionSet(params) };
};

} // namespace methods::MutationHas

class [[nodiscard("unnecessary construction")]] Mutation final
	: public service::Object
{
private:
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveUser(service::ResolverParams&& params) const;
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveSession(service::ResolverParams&& params) const;
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveItem(service::ResolverParams&& params) const;
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveItemGroup(service::ResolverParams&& params) const;
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveItemBrand(service::ResolverParams&& params) const;

	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolve_typename(service::ResolverParams&& params) const;

	struct [[nodiscard("unnecessary construction")]] Concept
	{
		virtual ~Concept() = default;

		virtual void beginSelectionSet(const service::SelectionSetParams& params) const = 0;
		virtual void endSelectionSet(const service::SelectionSetParams& params) const = 0;

		[[nodiscard("unnecessary call")]] virtual service::AwaitableObject<std::shared_ptr<UserMutation>> applyUser(service::FieldParams&& params) const = 0;
		[[nodiscard("unnecessary call")]] virtual service::AwaitableObject<std::shared_ptr<SessionMutation>> applySession(service::FieldParams&& params) const = 0;
		[[nodiscard("unnecessary call")]] virtual service::AwaitableObject<std::shared_ptr<ItemMutation>> applyItem(service::FieldParams&& params) const = 0;
		[[nodiscard("unnecessary call")]] virtual service::AwaitableObject<std::shared_ptr<ItemGroupMutation>> applyItemGroup(service::FieldParams&& params) const = 0;
		[[nodiscard("unnecessary call")]] virtual service::AwaitableObject<std::shared_ptr<ItemBrandMutation>> applyItemBrand(service::FieldParams&& params) const = 0;
	};

	template <class T>
	struct [[nodiscard("unnecessary construction")]] Model final
		: Concept
	{
		explicit Model(std::shared_ptr<T> pimpl) noexcept
			: _pimpl { std::move(pimpl) }
		{
		}

		[[nodiscard("unnecessary call")]] service::AwaitableObject<std::shared_ptr<UserMutation>> applyUser(service::FieldParams&& params) const override
		{
			if constexpr (methods::MutationHas::applyUserWithParams<T>)
			{
				return { _pimpl->applyUser(std::move(params)) };
			}
			else
			{
				static_assert(methods::MutationHas::applyUser<T>, R"msg(Mutation::applyUser is not implemented)msg");
				return { _pimpl->applyUser() };
			}
		}

		[[nodiscard("unnecessary call")]] service::AwaitableObject<std::shared_ptr<SessionMutation>> applySession(service::FieldParams&& params) const override
		{
			if constexpr (methods::MutationHas::applySessionWithParams<T>)
			{
				return { _pimpl->applySession(std::move(params)) };
			}
			else
			{
				static_assert(methods::MutationHas::applySession<T>, R"msg(Mutation::applySession is not implemented)msg");
				return { _pimpl->applySession() };
			}
		}

		[[nodiscard("unnecessary call")]] service::AwaitableObject<std::shared_ptr<ItemMutation>> applyItem(service::FieldParams&& params) const override
		{
			if constexpr (methods::MutationHas::applyItemWithParams<T>)
			{
				return { _pimpl->applyItem(std::move(params)) };
			}
			else
			{
				static_assert(methods::MutationHas::applyItem<T>, R"msg(Mutation::applyItem is not implemented)msg");
				return { _pimpl->applyItem() };
			}
		}

		[[nodiscard("unnecessary call")]] service::AwaitableObject<std::shared_ptr<ItemGroupMutation>> applyItemGroup(service::FieldParams&& params) const override
		{
			if constexpr (methods::MutationHas::applyItemGroupWithParams<T>)
			{
				return { _pimpl->applyItemGroup(std::move(params)) };
			}
			else
			{
				static_assert(methods::MutationHas::applyItemGroup<T>, R"msg(Mutation::applyItemGroup is not implemented)msg");
				return { _pimpl->applyItemGroup() };
			}
		}

		[[nodiscard("unnecessary call")]] service::AwaitableObject<std::shared_ptr<ItemBrandMutation>> applyItemBrand(service::FieldParams&& params) const override
		{
			if constexpr (methods::MutationHas::applyItemBrandWithParams<T>)
			{
				return { _pimpl->applyItemBrand(std::move(params)) };
			}
			else
			{
				static_assert(methods::MutationHas::applyItemBrand<T>, R"msg(Mutation::applyItemBrand is not implemented)msg");
				return { _pimpl->applyItemBrand() };
			}
		}

		void beginSelectionSet(const service::SelectionSetParams& params) const override
		{
			if constexpr (methods::MutationHas::beginSelectionSet<T>)
			{
				_pimpl->beginSelectionSet(params);
			}
		}

		void endSelectionSet(const service::SelectionSetParams& params) const override
		{
			if constexpr (methods::MutationHas::endSelectionSet<T>)
			{
				_pimpl->endSelectionSet(params);
			}
		}

	private:
		const std::shared_ptr<T> _pimpl;
	};

	explicit Mutation(std::unique_ptr<const Concept> pimpl) noexcept;

	[[nodiscard("unnecessary call")]] service::TypeNames getTypeNames() const noexcept;
	[[nodiscard("unnecessary call")]] service::ResolverMap getResolvers() const noexcept;

	void beginSelectionSet(const service::SelectionSetParams& params) const override;
	void endSelectionSet(const service::SelectionSetParams& params) const override;

	const std::unique_ptr<const Concept> _pimpl;

public:
	template <class T>
	explicit Mutation(std::shared_ptr<T> pimpl) noexcept
		: Mutation { std::unique_ptr<const Concept> { std::make_unique<Model<T>>(std::move(pimpl)) } }
	{
	}

	[[nodiscard("unnecessary call")]] static constexpr std::string_view getObjectType() noexcept
	{
		return { R"gql(Mutation)gql" };
	}
};

} // namespace graphql::omnisphere::object

#endif // MUTATIONOBJECT_H
