#include "SessionMutationObject.h"
#include "AuthPayLoadObject.h"
#include "LogoutPayloadObject.h"

#include "graphqlservice/internal/Schema.h"

#include "graphqlservice/introspection/IntrospectionSchema.h"

#include <algorithm>
#include <functional>
#include <sstream>
#include <stdexcept>
#include <unordered_map>

using namespace std::literals;

namespace graphql::omnisphere {
namespace object {

SessionMutation::SessionMutation(std::unique_ptr<const Concept> pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames SessionMutation::getTypeNames() const noexcept
{
	return {
		R"gql(SessionMutation)gql"sv
	};
}

service::ResolverMap SessionMutation::getResolvers() const noexcept
{
	return {
		{ R"gql(Login)gql"sv, [this](service::ResolverParams&& params) { return resolveLogin(std::move(params)); } },
		{ R"gql(Logout)gql"sv, [this](service::ResolverParams&& params) { return resolveLogout(std::move(params)); } },
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } }
	};
}

void SessionMutation::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void SessionMutation::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver SessionMutation::resolveLogin(service::ResolverParams&& params) const
{
	auto argData = service::ModifiedArgument<omnisphere::SessionParams>::require("Data", params.arguments);
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getLogin(service::FieldParams { std::move(selectionSetParams), std::move(directives) }, std::move(argData));
	resolverLock.unlock();

	return service::ModifiedResult<AuthPayLoad>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver SessionMutation::resolveLogout(service::ResolverParams&& params) const
{
	auto argData = service::ModifiedArgument<omnisphere::LogoutParams>::require("Data", params.arguments);
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getLogout(service::FieldParams { std::move(selectionSetParams), std::move(directives) }, std::move(argData));
	resolverLock.unlock();

	return service::ModifiedResult<LogoutPayload>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver SessionMutation::resolve_typename(service::ResolverParams&& params) const
{
	return service::Result<std::string>::convert(std::string{ R"gql(SessionMutation)gql" }, std::move(params));
}

} // namespace object

void AddSessionMutationDetails(const std::shared_ptr<schema::ObjectType>& typeSessionMutation, const std::shared_ptr<schema::Schema>& schema)
{
	typeSessionMutation->AddFields({
		schema::Field::Make(R"gql(Login)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(AuthPayLoad)gql"sv)), {
			schema::InputValue::Make(R"gql(Data)gql"sv, R"md()md"sv, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(SessionParams)gql"sv)), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(Logout)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(LogoutPayload)gql"sv)), {
			schema::InputValue::Make(R"gql(Data)gql"sv, R"md()md"sv, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(LogoutParams)gql"sv)), R"gql()gql"sv)
		})
	});
}

} // namespace graphql::omnisphere
