User::User(std::unique_ptr<const Concept> pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames User::getTypeNames() const noexcept
{
	return {
		R"gql(User)gql"sv
	};
}

service::ResolverMap User::getResolvers() const noexcept
{
	return {
		{ R"gql(Code)gql"sv, [this](service::ResolverParams&& params) { return resolveCode(std::move(params)); } },
		{ R"gql(Name)gql"sv, [this](service::ResolverParams&& params) { return resolveName(std::move(params)); } },
		{ R"gql(Email)gql"sv, [this](service::ResolverParams&& params) { return resolveEmail(std::move(params)); } },
		{ R"gql(Entry)gql"sv, [this](service::ResolverParams&& params) { return resolveEntry(std::move(params)); } },
		{ R"gql(Phone)gql"sv, [this](service::ResolverParams&& params) { return resolvePhone(std::move(params)); } },
		{ R"gql(Employee)gql"sv, [this](service::ResolverParams&& params) { return resolveEmployee(std::move(params)); } },
		{ R"gql(IsActive)gql"sv, [this](service::ResolverParams&& params) { return resolveIsActive(std::move(params)); } },
		{ R"gql(IsLocked)gql"sv, [this](service::ResolverParams&& params) { return resolveIsLocked(std::move(params)); } },
		{ R"gql(CreatedBy)gql"sv, [this](service::ResolverParams&& params) { return resolveCreatedBy(std::move(params)); } },
		{ R"gql(SuperUser)gql"sv, [this](service::ResolverParams&& params) { return resolveSuperUser(std::move(params)); } },
		{ R"gql(CreateDate)gql"sv, [this](service::ResolverParams&& params) { return resolveCreateDate(std::move(params)); } },
		{ R"gql(UpdateDate)gql"sv, [this](service::ResolverParams&& params) { return resolveUpdateDate(std::move(params)); } },
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } },
		{ R"gql(LastUpdatedBy)gql"sv, [this](service::ResolverParams&& params) { return resolveLastUpdatedBy(std::move(params)); } },
		{ R"gql(PasswordNeverExpires)gql"sv, [this](service::ResolverParams&& params) { return resolvePasswordNeverExpires(std::move(params)); } },
		{ R"gql(ChangePasswordNextLogin)gql"sv, [this](service::ResolverParams&& params) { return resolveChangePasswordNextLogin(std::move(params)); } }
	};
}

void User::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void User::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver User::resolveEntry(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getEntry(service::FieldParams { std::move(selectionSetParams), std::move(directives) });
	resolverLock.unlock();

	return service::ModifiedResult<int>::convert(std::move(result), std::move(params));
}

